import React, { useState } from 'react'

const index = () => {

  const [text, setText] = useState('');

  return (
    <div>
      <input 
        placeholder="Enter some text" 
        onChange={(e => setText(e.target.value))}
      />
      {text}
    </div>
  )
}

export default index;
