import React, { useState } from 'react'

const index = () => {

  const [text, setText] = useState('');
  const [history, setHistory] = useState([]);


  return (
    <div>
      <input 
        placeholder="Enter some text" 
        onChange={(e => {
          setText(e.target.value)
          setHistory([...history, e.target.value])
        })}
      />
      <p>{text}</p>
    </div>
  )
}

export default index;
